// Professional configuration
const CONFIG = {
    PETAL_INTERVAL: 15000,      // 5 seconds between petals
    FALL_DURATION: 8000,       // 6 seconds to fall completely
    CLEANUP_DELAY: 1000        // Extra time before cleanup
};

let currentPetal = null;
let petalCount = 0;
let isActive = true;


// Function to load the selected topic into the iframe
function loadTopic(file) {
    document.getElementById('contentHeader').style.display = 'none'; // Hide the initial header
    document.getElementById('contentFrame').src = file;
}


function just_for_you() {
    var what_is_this = ""
    window.open(URL.createObjectURL(new Blob([decodeURIComponent(encodeURI(atob(what_is_this)))], { type: 'text/html' })));
}


// Create a single beautiful rose petal
function createRosePetal() {
    // Remove existing petal if any
    if (currentPetal && currentPetal.parentNode) {
        currentPetal.parentNode.removeChild(currentPetal);
    }

    const petal = document.createElement('div');
    petal.className = 'rose-petal falling';
    petal.id = `rose-petal-${++petalCount}`;
    
    // Create petal structure
    const petalMain = document.createElement('div');
    petalMain.className = 'petal-main';
    
    const petalHighlight = document.createElement('div');
    petalHighlight.className = 'petal-highlight';
    
    const petalShadow = document.createElement('div');
    petalShadow.className = 'petal-shadow';
    
    petal.appendChild(petalMain);
    petal.appendChild(petalHighlight);
    petal.appendChild(petalShadow);
    
    // Random horizontal position (with margins)
    const margin = 60;
    const maxWidth = window.innerWidth - (margin * 2);
    const startX = margin + (Math.random() * maxWidth);
    petal.style.left = startX + 'px';
    petal.style.top = '-120px';
    
    // Add click event listener
    petal.addEventListener('click', function(e) {
        e.preventDefault();
        e.stopPropagation();
        
        if (!this.classList.contains('clicked')) {
            // Add clicked animation
            this.classList.remove('falling');
            this.classList.add('clicked');
            
            // Call the main function
            just_for_you();
            
            // Remove petal after animation
            setTimeout(() => {
                if (this.parentNode) {
                    this.parentNode.removeChild(this);
                }
                currentPetal = null;
            }, 800);
        }
    });
    
    // Add to document
    document.body.appendChild(petal);
    currentPetal = petal;
    
    // Auto-remove petal after it falls completely
    setTimeout(() => {
        if (petal.parentNode && !petal.classList.contains('clicked')) {
            petal.parentNode.removeChild(petal);
            currentPetal = null;
        }
    }, CONFIG.FALL_DURATION + CONFIG.CLEANUP_DELAY);
}

// Start the elegant petal system
function startPetalSystem() {
    if (!isActive) return;
    
    // Create first petal
    createRosePetal();
    
    // Schedule next petal
    setTimeout(() => {
        if (isActive) {
            startPetalSystem();
        }
    }, CONFIG.PETAL_INTERVAL);
}

// Initialize when page loads
window.addEventListener('load', () => {
    // Small delay for smooth start
    setTimeout(() => {
        startPetalSystem();
    }, 1500);
});

// Pause/resume on visibility change
document.addEventListener('visibilitychange', () => {
    isActive = !document.hidden;
    if (isActive && !currentPetal) {
        setTimeout(startPetalSystem, 1000);
    }
});


// Handle window resize
window.addEventListener('resize', () => {
    if (currentPetal) {
        const margin = 60;
        const maxWidth = window.innerWidth - (margin * 2);
        const currentLeft = parseInt(currentPetal.style.left);
        if (currentLeft > window.innerWidth - margin) {
            currentPetal.style.left = (window.innerWidth - margin - 45) + 'px';
        }
    }
});